mapper.select:

select id, code, name, parent_id, status, deleted, description
from basic_category
where id = #{id,jdbcType=INTEGER}

mapper.update:
UPDATE basic_category t1, 
	(SELECT max(deleted) + 1 AS deleted, code FROM basic_category group by code) t2 
SET t1.deleted = t2.deleted WHERE t1.code = t2.code AND t1.id = #{id,jdbcType=INTEGER}
