mapper.update:

update system_attachment
set name = #{name,jdbcType=VARCHAR},
  real_path = #{realPath,jdbcType=VARCHAR},
  original_name = #{originalName,jdbcType=VARCHAR},
  type = #{type,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, name, real_path, original_name, type
from system_attachment
where id = #{id,jdbcType=INTEGER}

mapper.insert:
	insert into system_attachment (name, real_path, original_name
	,  type)
  values (#{name,jdbcType=VARCHAR}, #{realPath,jdbcType=VARCHAR}, #{originalName,jdbcType=VARCHAR} 

  ,(select code from system_code where lower(type)=lower('ATTACHMENT_TYPE') and value=#{typeName,jdbcType=VARCHAR})
  )
